import React from 'react';

const ImageCard = ( {image}) => {
    const tags = image.tags.split(',');
  return (
    <div className = "max-w-sm rounded-lg overflow-hidden shadow-2xl transform transition duration-500 hover:scale-110">
      <img src = {image.webformatURL} alt = "" className="w-full " />
      <div className="px-6 py-4 text-center text-gray-700 font-mono">
        <div className="font-bold text-gray-700 text-2xl mb-2 text-center">
          Photo By {image.user}
        </div>
        <ul>
          <li>
            <strong>Views: </strong>
            {image.views}
          </li>
          <li>  
            <strong>Downloads: </strong>
            {image.downloads}
          </li>
          <li>
            <strong>Likes: </strong>
            {image.likes}
          </li>
          <form onSubmit={onSubmit} className='w-full max-w-sm'>
            <div className='flex items-center border-b-2 border-teal-500 py-2'>
                <input onChange={e => setText(e.target.value)} className='appearance-none bg-transparent border-none w-full text-gray-700 mr-3 py-1 px-2 leading-tight focus:outline-none font-mono' type='text' placeholder='Search Image Term...' />
                <button className='flex-shrink-0 bg-teal-500 hover:bg-teal-700 border-teal-500 hover:border-teal-700 text-sm border-4 text-white py-1 px-2 rounded font-mono' type = "submit">
                    GoTo
                </button>
            </div>
        </form>
          <li>
            <strong>URL: </strong>
            {image.pageURL}
          </li>
        </ul>
      </div>
      <div className="px-6 py-4 text-center">
        {tags.map((tag, index) => (
            <span key = {index} className="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2 font-mono">
            #{tag}
          </span>
        ))}
      </div>
    </div>
  )
}

export default ImageCard;
